import React, {Component} from 'react'
import styles from './QuizCreator.module.css'
import Button from '../../components/UI/Button/Button'
import Input from '../../components/UI/Input/Input'
import Select from '../../components/UI/Select/Select'
import {createControl, validate, validateForm} from '../../form/formFramework'
import axios from 'axios'

function createOptionControl(number) {
    return createControl({
        label: `Вариант ${number}`,
        errorMessage: 'Значение не может быть пустым',
        id: number,
    }, {required: true})
}

function createFormControls() {
    return {
        question: createControl({
            label: 'Введите вопрос',
            errorMessage: 'Вопрос не может быть пустым'
        }, {required: true}),
        option1: createOptionControl(1),
        option2: createOptionControl(2),
        option3: createOptionControl(3),
        option4: createOptionControl(4),
    }
}

export default class QuizCreator extends Component {
    state = {
        quiz: [],
        quizTitle: [],
        isFormValid: false,
        rightAnswerId: 1,
        formControls: createFormControls()
    }

    submitHandler = event => {
        event.preventDefault()
    }

    addQuestionHandler = event => {
        // TODO: Разобрать данную функцию!!!
        event.preventDefault()

        const quiz = this.state.quiz.concat()
        const index = quiz.length + 1

        const {question, option1, option2, option3, option4} = this.state.formControls

        console.log(quiz[0] ? Object.values(quiz[0])[0] : 'Пока не определено')

        const questionItem = {
            question: this.state.formControls.question.value,
            id: index,
            rightAnswerId: this.state.rightAnswerId,
            answers: [
                { text: option1.value, id: option1.id },
                { text: option2.value, id: option2.id },
                { text: option3.value, id: option3.id },
                { text: option4.value, id: option4.id },
            ]
        }

        quiz.push(questionItem)

        this.setState({
            quiz,
            isFormValid: false,
            rightAnswerId: 1,
            formControls: createFormControls()
        })
    }

    createQuizHandler = async event => {
        try {
            await axios.post('https://react-quiz-f412a-default-rtdb.firebaseio.com/quizes.json', this.state.quiz)
            await axios.post('https://react-quiz-f412a-default-rtdb.firebaseio.com/quizesTitle.json', this.state.quizTitle)

            this.setState({
                quiz: [],
                isFormValid: false,
                rightAnswerId: 1,
                formControls: createFormControls()
              })
        } catch (e) {
            console.log(e)
        }
    }

    // createQuizHandler = event => {
    //     event.preventDefault()
    //
    //     axios.post('https://react-quiz-f412a-default-rtdb.firebaseio.com/quizes.json', this.state.quiz)
    //       .then(response => {
    //           console.log(response)
    //       }, error => console.log(error))
    // }

    changeHandler = (value, controlName) => {
        const formControls = { ...this.state.formControls }
        const control = { ...formControls[controlName] }

        control.touched = true
        control.value = value
        control.valid = validate(control.value, control.validation)

        formControls[controlName] = control

        this.setState({
            formControls,
            isFormValid: validateForm(formControls)
        })

        console.log(this.state.quiz)

    }

    renderControls() {
        return Object.keys(this.state.formControls)
          .map((controlName, index) => {
            const control = this.state.formControls[controlName]
              return (
                <>
                        <Input
                          key={index}
                          label={control.label}
                          value={control.value}
                          valid={control.valid}
                          shouldValidate={!!control.validation}
                          touched={control.touched}
                          errorMessage={control.errorMessage}
                          onChange={event => this.changeHandler(event.target.value, controlName)}
                        />
                </>
              )
          })
    }

    selectChangeHandler = event => {
        this.setState({
            rightAnswerId: +event.target.value
        })
    }

    quizTitleHandler = event => {
        const quizTitle = this.state.quizTitle.concat()
        let title = event.trim()

        if (!title) {
            title = `Тест №${quizTitle.length + 1}`
        }

        quizTitle.push(title)

        this.setState({
            quizTitle
        })
    }

    render() {
        const select = <Select
            label="Выберите правильный ответ"
            value={this.state.rightAnswerId}
            onChange={this.selectChangeHandler}
            options={[
                {text: 1, value: 1},
                {text: 2, value: 2},
                {text: 3, value: 3},
                {text: 4, value: 4},
            ]}
        />

        return (
          <div className={styles.QuizCreator}>
              <div>
                  <h1>Создание теста</h1>

                  <form onSubmit={this.submitHandler}>

                      {
                          !!this.state.quiz[0]
                            ? <h1>{this.state.quizTitle[this.state.quizTitle.length - 1]}</h1>
                            :
                          <div>
                              <label htmlFor="title">Введите имя теста:</label>&nbsp;
                              <input
                                id="title"
                                onChange={event => this.quizTitleHandler(event.target.value)}
                              />
                              <hr/>
                          </div>
                      }


                      { this.renderControls() }

                      { select }

                      <Button
                        type="primary"
                        onClick={this.addQuestionHandler}
                        disabled={!this.state.isFormValid}
                      >
                          Добавить вопрос
                      </Button>
                      <Button
                        type="success"
                        onClick={this.createQuizHandler}
                        disabled={this.state.quiz.length === 0}
                      >
                          Создать тест
                      </Button>
                  </form>
              </div>
          </div>
        )
    }
}
