        {/*  <strong>1.</strong>*/}
        {/*  How are you?*/}
        {/*  <i className={'fa fa-check success'} />*/}
        {/*</li>*/}
      </ul>
      <p>Правильно 4 из {props.quiz.length}</p>

      <div>
        <button>Повторить</button>
      </div>
    </div>
  )
}

export default FinishedQuiz
